import React from "react"
import ItemMenu from "../itemMenu/itemMenu"
import { useState, useEffect } from "react"
import { useParams} from "react-router-dom"
import ItemList from "../itemList/itemList"
import db from "../../firebase/firebase"
import { MoonLoader } from "react-spinners"
import {
  collection,
  getDocs,
  query,
  where
} from 'firebase/firestore';
import  {obtener}  from "./../../firebase/firebase_functions.jsx"
import "./ItemListContainer.css"
const ItemListContainer = () =>
{
    const [products, setProducts] = useState([]);
    const clientsCollection = collection(db, 'productos');
    const [isLoading,setLoading] = useState(true);
    console.log(products)
    const { categoryName,spec } = useParams()
    console.log(useParams())
    const [items, setItems] = useState([])
    useEffect(() => {

    }, [])
    useEffect(() => {
      if (categoryName !== undefined) {
        if (spec !== undefined) {
          let q = query(clientsCollection,where("categoria", "==",categoryName))
           q = query(clientsCollection,where("extra", "==",spec))
          getDocs(q)
          .then((querySnapshot) => {
          let clientsList = [];
          console.log(querySnapshot)
          clientsList = querySnapshot.docs.map(client => {
            let aux = {
              ...client.data(),
              id : client.id
            }
            return aux
          }
          )
          setItems(clientsList)
        })   
        }
        else
        {
          let q = query(clientsCollection,where("categoria", "==",categoryName))
          getDocs(q)
          .then((querySnapshot) => {
          let clientsList = [];
          console.log(querySnapshot)
          clientsList = querySnapshot.docs.map(client => {
            let aux = {
              ...client.data(),
              id : client.id
            }
            return aux
          }
          )
          setItems(clientsList)
          })   
        }
      }
      else
        {
        getDocs(clientsCollection)
        .then((querySnapshot) => {
          let clientsList = [];
          console.log(querySnapshot)
          clientsList = querySnapshot.docs.map(client => {
            let aux = {
              ...client.data(),
              id : client.id
            }
            return aux
          }
          )
          setItems(clientsList)
        })    
      }
   //   setTimeout(() => setLoading(false),1000)
    }, [categoryName],[spec])
    return(
        <div className="background">
            <div className="items-container">
              <ItemMenu category={categoryName}/>
              {
                isLoading ? <div className="loaderContainer"><HashLoader
                color="#F03C00"
                size={60}
              /></div> : <ItemList items={items} />
              }
          </div>
        </div>
    )
}

export default ItemListContainer
