import db from "./firebase.js"
import {
  // doc,
  // getDoc,

  collection,
  getDocs,

  query,
  where,
} from 'firebase/firestore';
const obtener = () => {db.collection("users").get()
.then(querySnapshot => {
  querySnapshot.forEach((doc) => {
    console.log(`${doc.id} => ${doc.data()}`);
  });
});}  
export {obtener};
