import db from "./firebase.js"
import { doc, getDoc } from "firebase/firestore";
const usersRef = db.collection("productos");
const obtener = () => {
  return usersRef.get()
    .then(querySnapshot => {
      const users = querySnapshot.docs.map(doc => doc.data());
      return users;
    })
    .catch(error => {
      console.log(error);
    });
};
export  {obtener}
