import React, { useEffect, useState } from 'react'
import { useParams } from "react-router-dom"
import PrincipalDisplay from '../principalDisplay/PrincipalDisplay'
import db from "./../../firebase/firebase"
import {
  collection,
  getDocs
} from 'firebase/firestore';
const ItemDetailContainer = () => {
    const [product, setProduct] = useState([])
    const clientsCollection = collection(db, 'productos');
    const { id } = useParams()
    useEffect( ()=>{
      getDocs(clientsCollection)
          .then((querySnapshot) => {
          let clientsList = [];
          console.log(querySnapshot)
          clientsList = querySnapshot.docs.map(client => {
            let aux = {
              ...client.data(),
              id : client.id
            }
          }

          )
          console.log(clientsList)
          setProduct(clientsList)
      })
      const productSelected = product.find( producto => parseInt(producto.id) === parseInt(id) )
      console.log(productSelected)
      setProduct(productSelected)

    }, [id])
    return (
      <PrincipalDisplay item={product}/>
    )
  }
  export default ItemDetailContainer
