import React from "react"
import ItemMenu from "../itemMenu/itemMenu"
import { useState, useEffect } from "react"
import { useParams} from "react-router-dom"
import ItemList from "../itemList/itemList"
import  {obtener}  from "../../firebase/firebase_functions"
import "./ItemListContainer.css"
const ItemListContainer = () =>
{
    const [, setClients] = useState([]);
    let products;
    obtener().then(producto => products = producto   )
    const { categoryName,spec } = useParams()
    console.log(useParams())
    const [items, setItems] = useState([])
    useEffect(() => {
        const productosFiltered = spec === undefined ?
        products.filter(
          (productos) => (productos.category === categoryName )):
        products.filter(
            (productos) => (productos.category === categoryName && productos.extra === spec))
        const task = new Promise((resolve, reject) => {
          setTimeout(() => {
            resolve(categoryName ? productosFiltered : products)
          }, 500)
        })

        task
          .then((res) => {
            setItems(res)
          })
          .catch((err) => {
            console.log("se rechazo")
          })

      }, [categoryName])
    return(
        <div className="background">
            <div className="items-container">
              <ItemMenu category={categoryName}/>
              <ItemList items={items} />
          </div>
        </div>
    )
}

export default ItemListContainer
