import { useReducer } from "react";
import { useState } from "react";
import Context from "./context";
export default function Provider(props) {
  const { children } = props;
  const  [store,setStore] = useState([])
  const addcart = (producto,cantidad) =>
  {
      if(store.products.find((product) => product.id === producto.id ) == undefined)
      {
        let aux1 = store;
        let aux = {
          ...producto,
         cantidad: 1 
        }
        aux1.push(aux)
        setStore(aux1)
      }
      else
      {
        let aux2 = store.products.map((product) => {
          if (product.id === producto.id) {
            product.cantidad = product.cantidad + 1;
          }})  
        setStore(aux2)
      }
      console.log(store)
  }
  const removeCart = (producto) =>
  {
    store.products = store.products.map((product,index) => {
      if (product.id === producto.id) {
        store.products.splice(index)
      }})  
  }
  const clearCart = () =>
  {
    setStore([])
  }
  return <Context.Provider value={{addcart,removeCart,clearCart}}>{children}</Context.Provider>;
}
