import firestore from "./firebase.js"
const obtener = () => {
  return firestore().collection("productos").get()
    .then(querySnapshot => {
      const productos = querySnapshot.docs.map(doc => doc.data());
      return productos;
    })
    .catch(error => {
      console.log(error);
    });
};
export  {obtener};
