import React from "react"
import ItemMenu from "../itemMenu/itemMenu"
import { useState, useEffect } from "react"
import { useParams} from "react-router-dom"
import ItemList from "../itemList/itemList"
import  {obtener}  from "./../../firebase/firebase_functions.jsx"
import "./ItemListContainer.css"
const ItemListContainer = () =>
{
    const [products, setProducts] = useState([]);
    useEffect(() => {
    obtener().then(result => setProducts(result));
    }, []);
    console.log(products)
    const { categoryName,spec } = useParams()
    console.log(useParams())
    const [items, setItems] = useState([])
    useEffect(() => {
        let productosFiltered = spec === undefined ?
        products.filter(
          (productos) => (productos.categoria === categoryName )):
        products.filter(
            (productos) => (productos.categoria === categoryName && productos.extra === spec))
        if (spec === undefined && categoryName === undefined) {
          productosFiltered = products;
          setItems(productosFiltered)
          console.log("entro")
        }
        else
        {
          const task = new Promise((resolve, reject) => {
            setTimeout(() => {
              resolve(categoryName ? productosFiltered : products)
            }, 500)
          })
          task
          .then((res) => {
            setItems(res)
          })
          .catch((err) => {
            console.log("se rechazo")
          })
        }
      }, [categoryName],[products])
    return(
        <div className="background">
            <div className="items-container">
              <ItemMenu category={categoryName}/>
              <ItemList items={items} />
          </div>
        </div>
    )
}

export default ItemListContainer
