import React, { useEffect, useState } from 'react'
import  products  from "../../productMock"
import { useParams } from "react-router-dom"
import ItemDetail from '../itemDetail/itemDetail.js'
import PrincipalDisplay from '../principalDisplay/PrincipalDisplay'
import db from "./../../firebase/firebase"
import {
  collection,
  getDocs
} from 'firebase/firestore';
const ItemDetailContainer = () => {
    const [product, setProduct] = useState({})
    const clientsCollection = collection(db, 'productos');
    const { id } = useParams()
    useEffect( ()=>{
      console.log(products)
      getDocs(clientsCollection)
          .then((querySnapshot) => {
          let clientsList = [];
          console.log(querySnapshot)
          clientsList = querySnapshot.docs.map(client => {
            let aux = {
              ...client.data(),
              id : client.id
            }
          })
      })
      const productSelected = products.find( producto => parseInt(producto.id) === parseInt(id) )
      console.log(productSelected)
      setProduct(productSelected)

    }, [id])
    return (
      <PrincipalDisplay id={id}/>
    )
  }
  export default ItemDetailContainer
