import React from "react"
import ItemMenu from "../itemMenu/itemMenu"
import { useState, useEffect } from "react"
import { useParams} from "react-router-dom"
import ItemList from "../itemList/itemList"
import db from "../../firebase/firebase"
import { MoonLoader } from "react-spinners"
import {
  collection,
  getDocs,
} from 'firebase/firestore';
import  {obtener}  from "./../../firebase/firebase_functions.jsx"
import "./ItemListContainer.css"
const ItemListContainer = () =>
{
    const [products, setProducts] = useState([]);
    const clientsCollection = collection(db, 'productos');
    const [isLoading,setLoading] = useState(true);
    useEffect(() => {
      getDocs(clientsCollection)
      .then((querySnapshot) => {
          const clientsList = [];
          console.log(querySnapshot)
          querySnapshot.forEach((client) => {
            clientsList.push(client.data());
          })
        setProducts(clientsList)
      })
    }, []);
    console.log(products)
    const { categoryName,spec } = useParams()
    console.log(useParams())
    const [items, setItems] = useState([])
    useEffect(() => {
        let productosFiltered = spec === undefined ?
        products.filter(
          (productos) => (productos.categoria === categoryName )):
        products.filter(
            (productos) => (productos.categoria === categoryName && productos.extra === spec))
        if (spec === undefined && categoryName === undefined) {
          productosFiltered = products;
          console.log("entro")
        }
        const task = new Promise((resolve, reject) => {
          setTimeout(() => {
            resolve(categoryName ? productosFiltered : products)
            setLoading(false)
          }, 2000)
        })

        task
          .then((res) => {
            setItems(res)
          })
          .catch((err) => {
            console.log("se rechazo")
          })

      }, [categoryName])
    return(
        <div className="background">
            <div className="items-container">
              <ItemMenu category={categoryName}/>
              {
                isLoading ? <MoonLoader> Cargando </MoonLoader> : <ItemList items={items} />
              }
          </div>
        </div>
    )
}

export default ItemListContainer
