import db from "./firebase.js"
import { doc , getDoc } from "firebase/firestore"
import firebase from "firebase/firestore";
const productosRef = firebase().collection('productos');
const obtener = () => {
  return productosRef.get()
    .then(querySnapshot => {
      const productos = querySnapshot.docs.map(doc => doc.data());
      return productos;
    })
    .catch(error => {
      console.log(error);
    });
};
export  {obtener};
