import React from 'react'
import { useContext } from "react";
import Context from "../provider/context"
import "./resumePrice.css"
export default function ResumePrice() {
const { store} = useContext(Context);
  let totalprice = 0;
  store.map((product, index) => {
    totalprice += product.precio
  })
  return (
    <div className='totalprice'>
        <h2>Factura</h2>
        {store.map((product, index) => (
                        <div key={product.id} className="product__container">
                          {product.nombre} <strong>{product.quantity}X {product.precio}</strong>
                        </div>
                      ))}
        <h3>Precio total:</h3><h1>{totalprice}</h1>
    </div>
  )
}
