import React from "react"
import ItemMenu from "../itemMenu/itemMenu"
import { useState, useEffect } from "react"
import { useParams} from "react-router-dom"
import ItemList from "../itemList/itemList"
import db from "../../firebase/firebase"
import { MoonLoader } from "react-spinners"
import {
  collection,
  getDocs,
  query,
  where
} from 'firebase/firestore';
import  {obtener}  from "./../../firebase/firebase_functions.jsx"
import "./ItemListContainer.css"
const ItemListContainer = () =>
{
    const [products, setProducts] = useState([]);
    const clientsCollection = collection(db, 'productos');
    const [isLoading,setLoading] = useState(false);
    console.log(products)
    const { categoryName,spec } = useParams()
    console.log(useParams())
    const [items, setItems] = useState([])
    useEffect(() => {

      if (categoryName !== undefined) {
        if (spec !== undefined) {
          let productFiltred = products.filter((producto) => 
            (producto.categoria == categoryName && producto.extra == spec)
           )
          setItems(productFiltred)
        }
        else
        {
          let productFiltred = products.filter((producto) => 
            (producto.categoria == categoryName)
           )
          setItems(productFiltred)
        }
      }
    }, [categoryName],[spec])
    return(
        <div className="background">
            <div className="items-container">
              <ItemMenu category={categoryName}/>
              {
                isLoading ? <MoonLoader> Cargando </MoonLoader> : <ItemList items={items} />
              }
          </div>
        </div>
    )
}

export default ItemListContainer
